Content-Length: 48

{"hello":"Hello","year":2019,"language":"scala"}

fusion {
  # Fusion系统名，同时也设置为ActorSystem的name
  name = "fusion"
  # Fusion内建模块（Guice）
  module += "fusion.core.inject.builtin.BuiltinModule"
}
